$(document).ready(function () {
  waitForLayout(function() {
    layoutParser.parse(layout);
    setSize();
    layoutParser.loadData();
    layoutParser.queueReloadData(15 * 60 * 1000); // 15 minutes;
  	setTime("#timewidget");
  });
  // setTimeout("videoPlayer.queueRefresh()", 60 * 60 * 1000); // 60 * 60 * 1000;
});

function setSize() {
  var c = $(window).width();
  var d = 720;
  var a = parseInt($("body").css("font-size"));
  var e = c / d * a;
  $("body").css({
    "font-size": e + "px"
  });
  for (var b = tickers.length - 1; b >= 0; b--) {
    tickers[b].setSize()
  }
};

function waitForLayout(callback) {
  if (!window.Cocoa) {
    appendScript("js/layout.js");
  }
  if(typeof(layout) != "undefined") {
    callback();
  } else {
    setTimeout(function() {waitForLayout(callback)}, 1000);
  }
}